declare(strict_types=1);

namespace ZendTest\Expressive\Tooling\Factory\TestAsset;

class InvokableObject
{
    /**
     * @var array
     */
    public $options;

    /**
     * @param array $options
     */
    public function __construct(array $options = [])
    {
        $this->options = $options;
    }

    /**
     * @return array
     */
    public function getOptions()
    {
        return $this->options;
    }
}
